import axios from 'axios';

const U = "http://localhost:8080/api/v1/courses";
//const U = "http://localhost:3000/Courses";


class CourseService {
    // CRUD operation

    getCourses() {
        return axios.get(U);
    }

    getCoursesById(id) {
        console.log(id);
        return axios.get(U + '/' + id);
    }

    createCourse(data) {
        console.log(data)
        return axios.post(U, data);
    }

    updateCourse(id, course) {
        console.log(id)
        return axios.put(U + '/' + id, course);
    }


    deleteCourse(courseId) {
       return axios.delete(U + '/' + courseId);
    }
}

export default new CourseService();
